import React from 'react';

// Sample JSON dataset
import dataset from '../dataset.json';

const calculateMean = (data, className) => {
  const filteredData = data.filter(item => item.Alcohol === className);
  const flavanoidsSum = filteredData.reduce((sum, item) => sum + item.Flavanoids, 0);
  const mean = parseFloat(flavanoidsSum / filteredData.length);
  return mean.toFixed(3);
};

const calculateMedian = (data, className) => {
  const filteredData = data.filter(item => item.Alcohol === className);
  const sortedFlavanoids = filteredData.map(item => item.Flavanoids).sort();
  const mid = Math.floor(sortedFlavanoids.length / 2);
  const median = sortedFlavanoids.length % 2 === 0
    ? (sortedFlavanoids[mid - 1] + sortedFlavanoids[mid]) / 2
    : sortedFlavanoids[mid];
  return median.toFixed(3);
};

const calculateMode = (data, className) => {
  const filteredData = data.filter(item => item.Alcohol === className);
  const flavanoidsCountMap = {};
  filteredData.forEach(item => {
    if (flavanoidsCountMap[item.Flavanoids]) {
      flavanoidsCountMap[item.Flavanoids]++;
    } else {
      flavanoidsCountMap[item.Flavanoids] = 1;
    }
  });
  let mode = null;
  let maxCount = 0;
  Object.entries(flavanoidsCountMap).forEach(([flavanoid, count]) => {
    if (count > maxCount) {
      mode = flavanoid;
      maxCount = count;
    }
  });
  return parseFloat(mode).toFixed(3);
};

const Table = () => {
  const uniqueClasses = Array.from(new Set(dataset.map(item => item.Alcohol)));

  return (
    <table>
      <thead>
        <tr>
          <th>Measure</th>
          {uniqueClasses.map(className => (
            <th key={className}>Class {className}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>Flavanoids Mean</td>
          {uniqueClasses.map(className => (
            <td key={className}>{parseFloat(calculateMean(dataset, className))}</td>
          ))}
        </tr>
        <tr>
          <td>Flavanoids Median</td>
          {uniqueClasses.map(className => (
            <td key={className}>{calculateMedian(dataset, className)}</td>
          ))}
        </tr>
        <tr>
          <td>Flavanoids Mode</td>
          {uniqueClasses.map(className => (
            <td key={className}>{calculateMode(dataset, className)}</td>
          ))}
        </tr>
      </tbody>
    </table>
  );
};

export default Table;
