import React from "react";
import data from "./data.json";

function calculateMean(data, className) {
  const filteredData = data.filter((item) => item.Alcohol === className);
  const sum = filteredData.reduce((total, item) => total + item.Flavanoids, 0);
  return (sum / filteredData.length).toFixed(3);
}

function calculateMedian(data, className) {
  const filteredData = data
    .filter((item) => item.Alcohol === className)
    .map((item) => item.Flavanoids)
    .sort();
  const length = filteredData.length;
  const middleIndex = Math.floor(length / 2);

  if (length % 2 === 0) {
    return ((filteredData[middleIndex - 1] + filteredData[middleIndex]) / 2).toFixed(3);
  } else {
    return (filteredData[middleIndex]).toFixed(3);
  }
}

function calculateMode(data, className) {
  const filteredData = data.filter((item) => item.Alcohol === className);
  const counts = {};
  let maxCount = 0;
  let mode = null;

  filteredData.forEach((item) => {
    if (counts[item.Flavanoids]) {
      counts[item.Flavanoids]++;
    } else {
      counts[item.Flavanoids] = 1;
    }

    if (counts[item.Flavanoids] > maxCount) {
      maxCount = counts[item.Flavanoids];
      mode = item.Flavanoids;
    }
  });

  return mode.toFixed(3);
}

function ClassWiseStatistics() {
  const uniqueClasses = [...new Set(data.map((item) => item.Alcohol))];

  return (
    <table>
      <thead>
        <tr>
          <th>Class</th>
          <th>Mean</th>
          <th>Median</th>
          <th>Mode</th>
        </tr>
      </thead>
      <tbody>
        {uniqueClasses.map((className) => (
          <tr key={className}>
            <td>{className}</td>
            <td>{calculateMean(data, className)}</td>
            <td>{calculateMedian(data, className)}</td>
            <td>{calculateMode(data, className)}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default ClassWiseStatistics;
