import React from 'react';
import data from './dataset.json';
class GammaStatistics extends React.Component {
  calculateGamma = (data) => {
    return data.map((point) => {
      const gamma = (point.Ash * point.Hue) / point.Magnesium;
      return { ...point, Gamma: gamma };
    });
  };

  calculateClassStats = (data) => {
    const stats = {};
    data.forEach((point) => {
      const className = point.Class;
      if (!stats[className]) {
        stats[className] = [];
      }
      stats[className].push(point.Gamma);
    });
    return stats;
  };

  calculateMean = (values) => {
    const sum = values.reduce((acc, val) => acc + val, 0);
    return sum / values.length;
  };

  calculateMedian = (values) => {
    const sortedValues = [...values].sort((a, b) => a - b);
    const middleIndex = Math.floor(sortedValues.length / 2);
    if (sortedValues.length % 2 === 0) {
      return (sortedValues[middleIndex - 1] + sortedValues[middleIndex]) / 2;
    } else {
      return sortedValues[middleIndex];
    }
  };

  calculateMode = (values) => {
    const counts = {};
    values.forEach((value) => {
      if (!counts[value]) {
        counts[value] = 0;
      }
      counts[value]++;
    });

    let mode = null;
    let maxCount = 0;
    Object.entries(counts).forEach(([value, count]) => {
      if (count > maxCount) {
        mode = value;
        maxCount = count;
      }
    });

    return mode;
  };

  renderTable = (stats) => {
    const tableRows = Object.entries(stats).map(([className, values]) => (
      <tr key={className}>
        <td>Class {className}</td>
        <td>{this.calculateMean(values)}</td>
        <td>{this.calculateMedian(values)}</td>
        <td>{this.calculateMode(values)}</td>
      </tr>
    ));

    return (
      <table>
        <thead>
          <tr>
            <th>Measure</th>
            {Object.keys(stats).map((className) => (
              <th key={className}>Class {className}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Gamma</td>
            {tableRows}
          </tr>
        </tbody>
      </table>
    );
  };

  render() {
    const { data } = this.props;
    const gammaData = this.calculateGamma(data);
    const classStats = this.calculateClassStats(gammaData);

    return <div>{this.renderTable(classStats)}</div>;
  }
}

export default GammaStatistics;
